class Model():

    def __init__(self):
        self.model = keras.Sequential([
            layers.Rescaling(scale=1.0/255, input_shape=(256, 256, 3)),
            
            layers.Conv2D(filters=32, kernel_size=(3, 3), activation='relu', padding='same'),
            layers.BatchNormalization(),
            layers.MaxPooling2D((2, 2)),

            layers.Conv2D(filters=64, kernel_size=(3, 3), activation='relu', padding='same'),
            layers.BatchNormalization(),
            layers.MaxPooling2D((2, 2)),
            
            layers.Flatten(),
            layers.Dense(39, activation='softmax')
        ])
        self.load_weights()
        print(True)


    def load_weights(self):
        self.model.load_weights('80_percent_accuracy.weights.h5')

    def one_hot_max(arr):
        result = np.zeros_like(arr)
        result[np.argmax(arr)] = 1
        return result

    def model_predict(self, image):
        prediction = self.model.predict(image)
        prediction = self.one_hot_max(prediction)

        return hawktuah[prediction]

    @app.route('/api/process', methods=['POST'])
    def process_image(self):
        if 'image' not in request.files:
            return "No image provided", 400
        
        image_file = request.files['image']
        if image_file:
            img = Image.open(image_file)
            image_as_array = np.array(img.resize((256, 256)))  # Ensure resizing matches model input
            image_arr = np.expand_dims(image_as_array, axis=0)  # Add batch dimension
            output = self.model_instance.model_predict(image_arr)
            return {"result": output}
        return "Error processing image", 500

